from PIL import Image
import math

data = ''
MAX = int(math.sqrt(len(data)))

i = 0
B = Image.new('L',(10,10),255)
W = Image.new('L',(10,10),0)
np = Image.new('L',(MAX*10,MAX*10),0)

for x in range(MAX):
    for y in range(MAX):
        if data[i] == '0':
            np.paste(B,(10*x,10*y))
        else:
            np.paste(W,(10*x,10*y))
        i += 1

np.save('r.png')
np.show()
